import React, { useState } from "react";
import "../styles/collapsible.css";
import ArrownDown from '../assets/ArrowDown.svg';
import ArrowUp from '../assets/ArrowUp.svg';

//creer size md lg xs

export const Collapsible = ({ title, content, className }) => {
  const [open, setOPen] = useState(false);
  const toggle = () => {
    setOPen(!open);
  };

  return (
  <div>
    <span>
      <button onClick={toggle} className={`${className}CollapseButton`}> {title}</button>
    </span>
    {open && (
    <h4 className="CollapseText">{content}</h4>
    )}
  </div>
  );
};
