import React from "react";
import Houses from "../data/houses.json";
import { useParams } from "react-router-dom";
import { Collapsible } from "../components/Collapsible";
import PageNotFound from "./404";
import "../styles/houseDetails.css";
import Rating from "../components/Rating";
import Host from "../components/Host";
import Tags from "../components/Tags";
import Carousel from "../components/Carousel";

export default function PageDetails() {
  let urlId = useParams().id;
  let house = Houses.filter(function (house) {
    return house.id === urlId;
  });

  if (house[0] === undefined) {
    return <PageNotFound />;
  }

  // - commenter le code
  // ajout key, revoir warning console
  // lint everything

  const {
    title,
    location,
    description,
    pictures,
    equipments,
    rating,
    host,
    tags,
  } = house[0];

  return (
    <main>
      <Carousel pictures={pictures} />
      <section className="AllInfo">
        <div className="HouseInfo">
          <h1 className="Title">{title}</h1>
          <h2 className="Location">{location}</h2>
          <Tags tags={tags} />
        </div>
        <div className="HostInfo">
          <Host hostName={host.name} hostProfilePicture={host.picture} />
          <Rating rating={rating} />
        </div>
      </section>
      <section className="AllShortCollapse">
        <Collapsible
          className="Short"
          title="Description"
          content={description}
        />
        <Collapsible
          className="Short"
          title="Equipements"
          content={equipments}
        />
      </section>
    </main>
  );
}
