import React from "react";
import Banner from "../components/Banner";
import Houses from "../data/houses.json";
import { useParams } from "react-router-dom";
import { Collapsible } from "../components/Collapsible";
import PageNotFound from "./404";
import "../styles/houseDetails.css";
import Rating from "../components/Rating";

export default function PageDetails() {
  let urlId = useParams().id;
  // const arrayOfHouse = Houses.filter(checkId(house));

  let newArray = Houses.filter(function(house){
    return house.id == urlId;
  })

  const {
    cover,
    title,
    location,
    description,
    equipments,
    rating,
    host,
    tags,
  } = newArray;

  // function checkId(house) {
  //   if (house.id === urlId) return house;
  //   return PageNotFound;
  // }

  return (
    <main>
      <Banner
        image={cover}
        altText="Paysage avec montagne"
        description=""
        className="BannerDetails"
      />
      <h1>{title}</h1>
      <h2>{location}</h2>
      <p>{tags}</p>
      <Rating />
      <div className="AllShortCollapse">
        <Collapsible
          className="Short"
          title="Description"
          content={description}
        />
        <Collapsible
          className="Short"
          title="Equipements"
          content={equipments}
        />
      </div>
    </main>
  );
}
