import React, { useState } from "react";

export const Collapsible = ({ title, content }) => {
  const [open, setOPen] = useState(false);
  const toggle = () => {
    setOPen(!open);
  };

  return (
  <div>
    <button onClick={toggle}>{title}</button>
    {open && (
      <div>
        <h4>{content}</h4>
      </div>
    )}
  </div>
  );
};
