import React from "react";
import Banner from "../components/Banner";
import BannerAbout from "../assets/BannerAbout.png";
import "../styles/home.css";
import { Collapsible } from "../components/Collapsible";

export default function About() {
  return (
    <main>
      <Banner
        image={BannerAbout}
        altText="Paysage avec montagne"
        description=""
        className="BannerHome"
      />
      <Collapsible width="100%" title="Fiabilité" content="Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées  par nos équipes."/>
      <Collapsible className="Large" title="Respect" content="La bienveillance fait partie des valeurs fondatrices de Kasa. Tout comportement discriminatoire ou de perturbation du voisinage entraînera une exclusion de notre plateforme."/>
      <Collapsible className="Large" title="Service" content="Nos équipes se tiennent à votre disposition pour vous fournir une expérience parfaite. N'hésitez pas à nous contacter si vous avez la moindre question."/>
      <Collapsible className="Large" title="Sécurité" content="La sécurité est la priorité de Kasa. Aussi bien pour nos hôtes que pour les voyageurs, chaque logement correspond aux critères de sécurité établis par nos services. En laissant une note aussi bien à l'hôte qu'au locataire, cela permet à nos équipes de vérifier que les standards sont bien respectés. Nous organisons également des ateliers sur la sécurité domestique pour nos hôtes."/>
    </main>
  );
}
