import { useState } from "react";
import ArrowRight from "../assets/ArrowRight.svg";
import ArrowLeft from "../assets/ArrowLeft.svg";
import "../styles/carousel.css";

function Carousel({ pictures }) {
  const [current, setCurrent] = useState(0);
  const length = pictures.length;

  function nextPicture() {
    setCurrent(current === length - 1 ? 0 : current + 1);
  }

  function prevPicture() {
    setCurrent(current === 0 ? length - 1 : current - 1);
  }

  let indexPicture = current + 1;
  if (pictures.length > 1) {
    return (
      <section className="Carousel">
        <section>
          <img
            src={ArrowLeft}
            className="ArrowLeft"
            alt="accomodation"
            onClick={prevPicture}
          />
          <img
            src={ArrowRight}
            className="ArrowRight"
            alt="accomodation"
            onClick={nextPicture}
          />
        </section>
        {pictures.map((picture, index) => {
          return (
            <div key={index}>
              {index === current && (
                <img className="CarouselPicture" src={picture} alt="Pictures of the apartment selected" />
              )}
              <span>
                {indexPicture}/{pictures.length}
              </span>
            </div>
          );
        })}
      </section>
    );
  } else {
    return (
      <section>
        {pictures.map((picture, index) => {
          return (
            <div key={index}>
              {index === current && <img src={picture} alt="Pictures of the apartment selected" />}
              <span>
                {indexPicture}/{pictures.length}
              </span>
            </div>
          );
        })}
      </section>
    );
  }
}

export default Carousel;
