import React from "react";
import Banner from "../components/Banner";
import Houses from "../data/houses.json";
import { useParams } from "react-router-dom";
import { Collapsible } from "../components/Collapsible";

export default function PageDetails() {
  const urlId = useParams().id;
  const allHouses = Houses.map((houses) => houses);
  const selectedHouse = allHouses.find((house) => {
    return house.id === urlId;
  });
  const { cover, title, location, description, equipments, rating, host, tags } =
    selectedHouse;

  return (
    <main>
      <Banner
        image={cover}
        altText="Paysage avec montagne"
        description=""
        className="BannerHome"
      />
      <h1>{title}</h1>
      <h2>{location}</h2>
      <p>{tags[0]}</p>
      <Collapsible title='Description' content={description} />
      <Collapsible title='Equipements' content={equipments} />
    </main>
  );
}
