import React from "react";
import Banner from "../components/Banner";
import BannerHome from "../assets/BannerHome.png";
import { medias } from "../data/data.json";

export default function Home() {
  let medias = fetch("../data/data.json")

  return (
    <main>
      <Banner height='auto' image={BannerHome} altText='lol' description='Chez vous, partout et ailleurs'/>
      <div> {medias.map(({ id, cover, title }) => (<div key={id} image={cover} title={title}/>))} </div>
    </main>
  );
}
