import React, { useState } from "react";
import "../styles/collapsible.css";
import ArrownDown from '../assets/ArrowDown.svg';
import ArrowUp from '../assets/ArrowUp.svg';

export const Collapsible = ({ title, content, width }) => {
  const [open, setOPen] = useState(false);
  const toggle = () => {
    setOPen(!open);
  };

  return (
  <div>
    <span>
      <button onClick={toggle} className="CollapseButton">{title}</button>
      <img src={ArrownDown} />
    </span>
    {open && (
    <h4 className="CollapseText">{content}</h4>
    )}
  </div>
  );
};
