import { useState } from "react";
import ArrowRight from "../assets/ArrowRight.svg";
import ArrowLeft from "../assets/ArrowLeft.svg";
import "../styles/carousel.css"

function Carousel({ pictures }) {
  const [current, setCurrent] = useState(0);
  const length = pictures.length;

  function nextPicture() {
    setCurrent(current === length - 1 ? 0 : current + 1);
  }

  function prevPicture() {
    setCurrent(current === 0 ? length - 1 : current - 1);
  }

  let indexPicture = current + 1;
  if (pictures.length > 1) {
    return (
      <section className="Carousel">
        <img src={ArrowLeft} className="ArrowLeft" alt="accomodation" onClick={prevPicture} />
        <img src={ArrowRight} className="ArrowRight" alt="accomodation" onClick={nextPicture} />
        {pictures.map((picture, index) => {
          return (
            <div key={index}>
              {index === current && <img src={picture} alt="gallery" />}
              <div>
                {indexPicture}/{pictures.length}
              </div>
            </div>
          );
        })}
      </section>
    );
  } else {
    return (
      <section className="Carousel">
        {pictures.map((picture, index) => {
          return (
            <div key={index}>
              {index === current && <img src={picture} alt="gallery" />}
              <div>
                {indexPicture}/{pictures.length}
              </div>
            </div>
          );
        })}
      </section>
    );
  }
}

export default Carousel;
